const{src:src,dest:dest,series:series}=require("gulp"),htmlmin=require("gulp-html-minifier-terser"),terser=require("gulp-terser"),cleanCSS=require("gulp-clean-css"),globs={html:"./**/*.html",css:"./*.css",js:"./*.js"};function html(){return src(globs.html).pipe(htmlmin({collapseWhitespace:!0})).pipe(dest("dist"))}function css(){return src(globs.css).pipe(cleanCSS()).pipe(dest("dist/styles"))}function js(){return src(globs.js).pipe(terser()).pipe(dest("dist/scripts"))}exports.html=html,exports.css=css,exports.js=js,exports.default=series(html,css,js);